mapper.select:
select * from (
select rownum rn,a.* from
(select * from board order by num desc) a
)where rn between #{start} and #{end}

mapper.update:
update board set userid=#{userid},subject=#{subject},content=#{content:VARCHAR},
wdate=systimestamp

,filename=#{filename},filesize=#{filesize} 

where num=#{num}
